package week1.assignment;

public class FindOddorEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=25;
		if (i % 2==0)
		{
		System.out.println(i + " is even number");
		} 
		else
		{
        System.out.println(i + " is odd number");
		}

	}

}
